(10, 1, 14000, 14000, 'Site Category', ' ', '', 'acl-site', '<p>Site category is the highest level category used for permission assignment. Any unassigned content defaults to this category.</p>', 2, 0, 0, 1, '2012-09-13 12:00:00', '0000-00-00 00:00:00', 1, 0, 0, '2012-09-13 12:00:00', 1, '2012-09-13 12:00:00', 1, '0000-00-00 00:00:00', 0, 0, 0, 0, 0, 0, 0, '', '{}', '{}', 'en-GB', 0, 1),
(11, 1, 14000, 14000, 'Application Category', ' ', '', 'acl-application', '<p>Application category is the second highest category for permission assignment.Can be used to separate site-wide from application-specific permissioning requirements.</p>', 2, 0, 0, 1, '2012-09-13 12:00:00', '0000-00-00 00:00:00', 1, 0, 0, '2012-09-13 12:00:00', 1, '2012-09-13 12:00:00', 1, '0000-00-00 00:00:00', 0, 0, 0, 0, 0, 0, 0, '', '{}', '{}', 'en-GB', 0, 2),
(12, 1, 14000, 14000, 'Extension Category', ' ', '', 'acl-extension', '<p>The Extension Category provides default permissions for Extensions and related content.</p>', 2, 0, 0, 1, '2012-09-13 12:00:00', '0000-00-00 00:00:00', 1, 0, 0, '2012-09-13 12:00:00', 1, '2012-09-13 12:00:00', 1, '0000-00-00 00:00:00', 0, 0, 0, 0, 0, 0, 0, '', '{}', '{}', 'en-GB', 0, 3),
(13, 1, 14000, 14000, 'Content Category', ' ', '', 'acl-content-all', '<p>The Content Category can be used to provide permissions to those who oversee all content production and maintenance on the site.</p>', 2, 0, 0, 1, '2012-09-13 12:00:00', '0000-00-00 00:00:00', 1, 0, 0, '2012-09-13 12:00:00', 1, '2012-09-13 12:00:00', 1, '0000-00-00 00:00:00', 0, 0, 0, 0, 0, 0, 0, '', '{}', '{}', 'en-GB', 0, 4),
(14, 1, 14000, 14000, 'User and Categorization Category', ' ', '', 'acl-content-categorization', '<p>The User and Categorization Category covers creation and maintenance of User, Group, Category, and Tag data.</p>', 2, 0, 0, 1, '2012-09-13 12:00:00', '0000-00-00 00:00:00', 1, 0, 0, '2012-09-13 12:00:00', 1, '2012-09-13 12:00:00', 1, '0000-00-00 00:00:00', 0, 0, 0, 0, 0, 0, 0, '', '{}', '{}', 'en-GB', 0, 5),
(15, 1, 14000, 14000, 'Primary Data Category', ' ', '', 'acl-content', '<p>Includes all primary resource content but not categories, tags, users and groups or response content.</p>', 2, 0, 0, 1, '2012-09-13 12:00:00', '0000-00-00 00:00:00', 1, 0, 0, '2012-09-13 12:00:00', 1, '2012-09-13 12:00:00', 1, '0000-00-00 00:00:00', 0, 0, 0, 0, 0, 0, 0, '', '{}', '{}', 'en-GB', 0, 6),
(16, 1, 14000, 14000, 'Response Element Category', ' ', '', 'acl-response', '<p>Default permissions for all response elements, like ratings, comments, and notifications.</p>', 2, 0, 0, 1, '2012-09-13 12:00:00', '0000-00-00 00:00:00', 1, 0, 0, '2012-09-13 12:00:00', 1, '2012-09-13 12:00:00', 1, '0000-00-00 00:00:00', 0, 0, 0, 0, 0, 0, 0, '', '{}', '{}', 'en-GB', 0, 7),
